import alertify from 'alertifyjs';
import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Alert, Button, DropdownItem, DropdownMenu, DropdownToggle, NavItem, NavLink, UncontrolledDropdown } from 'reactstrap';
import { bindActionCreators } from 'redux';
import * as cartAction from "../../redux/actions/cartActions";

class CartSummary extends Component {
    renderEmpty() {
        return (
            <NavItem>
                <NavLink>Sepetiniz Boş</NavLink>
            </NavItem>
        )
    }

    removeCard = (product) =>{
        this.props.actions.removeFromCard(product);
        alertify.success(product.productName+" sepetden silindi");
    }

    renderSummary() {
        return (

            <UncontrolledDropdown nav inNavbar>
                <DropdownToggle nav caret>
                    {this.props.cart.length}' ürün Ekli
                </DropdownToggle>
                <DropdownMenu right>
                    {
                        this.props.cart.map(cartitem => (

                            <DropdownItem>
                                <Button color="danger" onClick={(e)=>this.removeCard(cartitem.product)}>X</Button>
                                <Alert color="primary">{cartitem.product.productName } { cartitem.quantity}</Alert>
                                
                            </DropdownItem>
                        ))
                      }



                    <DropdownItem divider />
                    <DropdownItem>
                        Sepete Git
                    </DropdownItem>
                </DropdownMenu>
            </UncontrolledDropdown>
        )
    }

    render() {
        return <div>
            {
                this.props.cart.length > 0 ? this.renderSummary() : this.renderEmpty()
            }

        </div>;
    }
}

function mapStateToProps(state) {
    return {
            cart: state.cartReducer
    }
}

//operasyonları redux almak için
function mapDispacthToProps(dispatch) {
    return {
        actions: {
            removeFromCard: bindActionCreators(cartAction.removeFromCart, dispatch)
        }
    }
}
export default connect(mapStateToProps,mapDispacthToProps)(CartSummary)
