import * as actionTypes from "../actions/actionTypes"
import intialState from "./intialState";

export default function UtilitiesReducer(state = intialState.backBtState,action)
{
   
    switch (action.type) {
        case actionTypes.ARANGE_BACK_BT_VISIBLE:
       
            return window.location.pathname.indexOf("/") < 0;
                
        default:
            return state;
    }
}
