import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Alert, DropdownItem, DropdownMenu, DropdownToggle, NavItem, NavLink, UncontrolledDropdown } from 'reactstrap';
import { bindActionCreators } from 'redux';
import * as cartAction from "../../redux/actions/cartActions";

class CartSummary extends Component {
    renderEmpty() {
        return (
            <NavItem>
                <NavLink>Sepetiniz Boş</NavLink>
            </NavItem>
        )
    }

    renderSummary() {
        return (

            <UncontrolledDropdown nav inNavbar>
                <DropdownToggle nav caret>
                    {this.props.actions.cart.length}' ürün Ekli
                </DropdownToggle>
                <DropdownMenu right>
                    {
                        this.props.actions.cart.map(cartitem => (

                            <DropdownItem>
                                <Alert color="primary">{cartitem.product.productName } { cartitem.quantity}</Alert>
                            </DropdownItem>
                        ))
                      }



                    <DropdownItem divider />
                    <DropdownItem>
                        Sepete Git
                    </DropdownItem>
                </DropdownMenu>
            </UncontrolledDropdown>
        )
    }

    render() {
        return <div>
            {
                this.props.actions.cart.length > 0 ? this.renderSummary() : this.renderEmpty()
            }

        </div>;
    }
}

function mapStateToProps(state) {
    return {
        actions: {
            cart: state.cartReducer
        }
    }
}

//operasyonları redux almak için
function mapDispacthToProps(dispatch) {
    return {
        actions: {
            removeFromCard: bindActionCreators(cartAction.removeFromCart, dispatch)
        }
    }
}
export default connect(mapStateToProps,mapDispacthToProps)(CartSummary)
