import React, { Component } from "react";
import { connect } from "react-redux";
import { Link } from "react-router-dom";
import { Button } from "reactstrap";
import { bindActionCreators } from "redux";
import * as cartAction from "../../redux/actions/cartActions";

class BackButton extends Component {
    render() { 
        return <div>
           <Button hidden={this.props.backButtonShowStateCart} color="info" size="sm"><Link to={"/"}>  Geri </Link></Button> 
        </div>;
    }
}

function mapStateToProps(state) {
    return {
            cart: state.cartReducer
    }
}

//operasyonları redux almak için
function mapDispacthToProps(dispatch) {
    return {
        actions: {
            backButtonShowStateCart: bindActionCreators(cartAction.backButtonShowStateCart, dispatch)
        }
    }
}
export default connect(mapStateToProps,mapDispacthToProps)(BackButton)
